function generateRandomString(length, includeNumbers = true) {
    const chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < length; i++) result += chars[Math.floor(Math.random() * chars.length)];
    return result;
}

function generateRealisticEmail(domain) {
    let username;
    switch (domain) {
        case "qq.com":
            username = Math.floor(100000000 + Math.random() * 900000000).toString();
            break;

        case "163.com":
            username = generateRandomString(6 + Math.floor(Math.random() * 4), true);
            if (Math.random() > 0.6) username = username.slice(0, 3) + '_' + username.slice(3);
            break;

        case "outlook.com":
            username = generateRandomString(6 + Math.floor(Math.random() * 4), true);
